        setScores(data);
    };

    return (
        <div>
            <h1>Tennis Score Kata</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={sequence}
                    onChange={e => setSequence(e.target.value.toUpperCase().replace(/[^AB]/g,""))}
